/* GENERAL */

body {
  background-color: #ffffff;
  margin: auto;
  font-family: 'Merriweather', serif;
  font-size: 1rem;
  color: #1c1c1c;
  text-align: center;
}


main{
  margin: auto;
}

.texts{
  padding: 1em;
}

.frases {
  background-color: #c6dae5;
  color: black;
  font-style: italic;
  font-weight: 300;
  padding: 2em;
  line-height: 2em;
}

/* HEADER*/

.hero-image {
  background-image:
  linear-gradient(rgba(0, 0, 0, 0.6),rgba(0, 0, 0, 0)),
      url("/img/hero.jpeg");
  height: 50%;
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  position: relative;
}

.hero-text {
  text-align: center;
  position: relative;
  font-weight: 600;
  top: 25%;
  left: 50%;
  transform: translate(-50%, 10%);
  color: black;
  min-height: 8em;
  font-size: 2.5em;
  color: white;
}

.hero-button {
  background-color: #5f7c79;
  text-decoration: none;
  border-style: none;
  font-family:  'Merriweather', serif;
  font-size: 0.5em;
  padding: 0.5em;
  width: 15em;
  border-radius: 2em;
  color: white;
}

.hero-button a {
  text-decoration: none;
  border-style: none;
  color: white;

}

.hero-button:hover {
  background-color: grey;
  text-decoration: none;
  border-style: none;
  color: white;
}


.boton {
  background-color: #5f7c79;
  text-decoration: none;
  border-style: none;
  font-family:  'Merriweather', serif;
  font-size: 1em;
  padding-top: 0.5em;
  padding-left: 2em;
  padding-right: 2em;
  padding-bottom: 0.5em;
  border-radius: 2em;
  color: white;
  margin-bottom: 2em;
}

.boton a {
  text-decoration: none;
  border-style: none;
  color: white;

}

.boton:hover {
  background-color: grey;
  text-decoration: none;
  border-style: none;
  color: white;
}


.menu{
  background-color: #5f7c79;
}

nav {
    text-decoration: none;
    font-family: 'Open sans', sans-serif;
}

nav ul, nav li {
	list-style: none;
  text-decoration: none;
  padding: 0 1em 0 1em;
  margin-top: 0;
}

a {
  text-decoration: none;
  color: white;
}

nav a {
  color: black;
  font-weight: 600;
  line-height: 2em;
  text-decoration: none;
  margin: 0;
}

nav a:hover {
  color: white;
}

#logo {
    font-family: 'Merriweather', serif;
    font-size: 2em;
    font-weight: 600;
    margin: auto;
    padding: 0.5em;
}

#logo a{
    color: white;
    text-decoration: none;
}

#logo a:hover{
    color: black;
}

/* TIPOGRAFIA*/

h1 {
  font-family: 'Merriweather', serif;
  font-size: 1.5em;
}

h2 {
  font-family: 'Merriweather', serif;
  font-weight: 400;
  font-size: 1.5em;
}
h5 {
  font-family: 'Merriweather', serif;
  font-size: 1rem;
  color: black;
  text-align: center;
}
.arriba {
  position: fixed;
  font-size: 1em;
  bottom: 1em;
  right: 1em;
  background-color: #82acbb;
  border-color: white;
  border-radius: 3em;
  width: 2em;
  height: 2em;
  text-align: center;
  padding: 1em;
}

/*INDEX*/

img {
  height: 12em;
}

img:hover {
  opacity: 0.5;
}

.datos {
  font-family: 'Merriweather', serif;
  font-size: 0.75rem;
  line-height: 2em;
}

/* FOOTER */

footer {
	color: white;
	background-color: #5f7c79;
  padding: 1em;
  font-family: 'Open sans', sans-serif;
  list-style: none;
  bottom: 0;
}

.footer ul, .acreditacion ul {
  list-style: none;
  text-decoration: none;
  display: flex;
  justify-content: center;
}

.footer {
  font-size: 0.75rem;
  line-height: 180%;
}

.footer a, .acreditacion a {
	text-decoration: none;
	color: white;
  padding: 1em;
}

.footer a:hover{
  color: #82acbb;
}

footer img{
  width: 5em;
  height: 5em;
  padding: 0.5em;
}

.acreditacion{
  font-size: 0.75rem;
  line-height: 2em;
  margin-top: 2em;
  display: block;
  text-align: center;
}

.acreditacion ul{
  padding: 0;
}


footer {
  padding: 1em;
}

.footer {
  display: inline-block;
  justify-content: space-between;
  align-items: center;
  display: inline-block;
}

.footer ul, .footer li {
  display: block;
  padding: 0;
}

#rrss{
  padding-right: 1em;
  display: block;
}


.footer li::after {
  font-weight: 700;
  padding-right: 1em;
}

li:last-child:after{
  display: none;
}



/*LISTADO RECETA*/

.listado {
  text-align: center;
}

.listado a {
  text-decoration: none;
  color: white;
}

.listado button {
  background-color: #5f7c79;
  text-decoration: none;
  border-style: none;
  color: white;
  font-family:  'Merriweather', serif;
  font-size: 1em;
  padding: 0.5em;
  width: 10em;
  border-radius: 2em;
  text-align: center;
}

.listado button:hover {
  background-color: grey;
}

.listado h1 {
  text-align: center;
}

.listado h2 {
  color: #5f7c79;
}

.box {
  width: 100%;
  height: 300px;
}

.box img {
  height: 100%;
  width: 100%;
}

.cover {
  object-fit: cover;
}

.listado p {
  margin: 1em;
}
/* DETALLES */

h4 {
  font-family: 'Open sans', sans-serif;
  font-size: 18px;
  font-weight: 700;
}

aside {
  text-align: left;
  margin-right: 2em;
  margin-bottom: 2em;
}

.apartados {
  text-align: left;
}

.apartados p {
  font-family: 'Open sans', sans-serif;
  font-weight: 600;
  padding-bottom: 0.5em;
}

.container {
  margin-bottom: 1em;
  text-align: center;
}


/*DETALLE RECETA*/

h2 {
  font-weight: 700;
  text-align: left;
}

h3 {
  font-weight: 700;
  border-bottom: 1px solid black;
  text-align: left;
}

.detalles p {
  line-height: 1.5em;
  padding-left: 1em;
}

.explicacion p {
  text-align: left;
  line-height: 1.5em;
}


.recetas img {
  width: 100%;
  max-width: 800px;
  height: 500px;
}

h2{
  text-align: center;
}

.ficha {
  padding: 1em;
}

.anterior {
  background-color: #5f7c79;
  text-decoration: none;
  border-style: none;
  color: white;
  font-family:  'Merriweather', serif;
  font-size: 1em;
  padding: 0.5em;
  width: 10em;
  border-radius: 2em;
  float:left;
  margin-bottom: 1em;
  margin-left: 1em;
}

.siguiente {
  background-color: #5f7c79;
  text-decoration: none;
  border-style: none;
  color: white;
  font-family:  'Merriweather', serif;
  font-size: 1em;
  padding: 0.5em;
  width: 10em;
  border-radius: 2em;
  float: right;
  margin-right: 1em;
  margin-bottom: 1em;
}

button:hover{
  background-color: grey;
}

.nosotros p {
    width:80%;
    margin: auto;
    padding-bottom: 3em;
    padding-top: 2em;
}

@media screen and (min-width: 900px) {

  /*GENERAL*/

  body {
    padding:0;
  }

  .menu {
    display: flex;
    flex-direction: row;
    align-items: center;
    width: 100%;
    box-sizing: border-box;
    justify-content: space-between;
    background-color: #5f7c79;
  }

  .menu :nth-child(1) {
    order: 1;
  }

  .menu :nth-child(2) {
    order: 2;
    margin-right: 2em;
  }

  .frases {
    text-align: center;
  }

  #logo {
      margin-left: 2em;
  }


  nav {
    text-decoration: none;
    font-family: 'Open sans', sans-serif;
    color: #1c1c1c;
  }

  nav ul, nav li, nav a {
    display: inline-block;
  	list-style: none;
    text-decoration: none;
    color: black;
    margin-bottom: 0;
  }

  nav li{
  padding-bottom: 0
  }


  nav a:hover{
    opacity: 0.5;
  }

  footer {
    padding: 1em;
  }

  .footer ul, .footer li {
    display: flex;
  }

  .footer {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }

  #rrss{
    padding-right: 1em;
  }


  .footer li::after {
    font-weight: 700;
    padding-right: 1em;
    padding-left: 1em;
  }

  li:last-child:after{
    display: none;
  }

  .acreditacion{
    text-align: left;
  }



  /*INDEX*/

  .descripcion {
    max-width: 50vw;
    margin: auto;
  }

  figure {
    display: inline-block;
  }

  section {
    padding-bottom: 2em;
  }


  /*LISTADO RECETA*/

  .listado {
    max-width: 80%;
    display: flex;
    flex: 90%;
    flex-wrap: wrap;
    margin: auto;
    justify-content: center;
  }

  .listado h2 {
    font-size: 20px;
    margin-bottom: 0;
  }

  .listado p{
    text-align: center;
  }

  .container {
    width: 40%;
    text-align: center;
    margin: 0 1em 1em 1em;
  }

  .box {
    height: 50%;
    margin-bottom: 0;
    text-align: center;
  }

  .box img {
    object-fit: cover;
  }


  /*DETALLE RECETA*/
  .ficha {
    display: grid;
    grid-template-columns: 2fr 3fr;
    grid-gap: 20px;
    width: 70%;
    margin: auto;
  }

  .explicacion {
    padding-bottom: 0;
    margin-left: 0;
  }

  .explicacion p {
    width: 100%;
    margin-right: 0;
    margin-left: 0;
    text-align: left;
  }
}
