/* GENERAL */

body {
  background-color: #ffffff;
  margin: auto;
  font-family: 'Merriweather', serif;
  font-size: 1rem;
  color: #1c1c1c;
  text-align: center;
}


main{
  margin: auto;
}

.texts{
  padding: 1em;
}

.frases {
  background-color: grey;
  color: white;
  font-style: italic;
  font-weight: 300;
  padding: 1em;
  line-height: 2em;
}

/* HEADER*/


img {
  height: 200px;
}

.hero-image {
  background-image: url("/img/hero-mobile.jpg");
  height: 50%;
  background-repeat: no-repeat;
  background-size: cover;
  clip-path: polygon(100% 0%, 100% 89%, 91% 98%, 79% 91%, 68% 100%, 53% 91%, 41% 100%, 26% 90%, 15% 100%, 0 89%, 0% 0%);
  min-height: 600px;
}

.hero-text {
  text-align: center;
  position: relative;
  font-weight: 700;
  top: 25%;
  left: 50%;
  transform: translate(-50%, 10%);
  color: black;
  min-height: 8em;
  font-size: 2.5em;
  color: white;
}

.hero-button {
  background-color: #5f7c79;
  text-decoration: none;
  border-style: none;
  font-family:  'Merriweather', serif;
  font-size: 0.5em;
  padding: 0.5em;
  width: 15em;
  border-radius: 2em;
  color: white;
}

.hero-button a {
  text-decoration: none;
  border-style: none;
  color: white;

}

.hero-button:hover {
  background-color: grey;
  text-decoration: none;
  border-style: none;
  color: white;
}

.boton {
  background-color: #5f7c79;
  text-decoration: none;
  border-style: none;
  font-family:  'Merriweather', serif;
  font-size: 1em;
  padding-top: 0.5em;
  padding-left: 2em;
  padding-right: 2em;
  padding-bottom: 0.5em;
  border-radius: 2em;
  color: white;
  margin-bottom: 2em;
  transition: background-color 0.5s ease-out;
}

.boton a {
  text-decoration: none;
  border-style: none;
  color: white;

}

.boton:hover {
  background-color: #e5924e;
  text-decoration: none;
  border-style: none;
  color: white;
}


.menu{
  background-color: #5f7c79;
}

a {
  text-decoration: none;
  color: white;
}

nav {
    text-decoration: none;
    font-family: 'Open sans', sans-serif;
}

nav ul {
	list-style: none;
  text-decoration: none;
  padding: 0 1em 0 1em;
  margin-top: 0;
}

nav li {
  position: relative;
  padding-bottom: 0;
  justify-content: center;
}

nav li:last-child {
  position: relative;
  padding-bottom: 1em;
  justify-content: center;
}

nav a {
  color: black;
  font-weight: 700;
  line-height: 2em;
  text-decoration: none;
}

nav a:hover {
  color: white;
}

nav a {
  color: #fff;
  text-decoration: none;
  display: inline-block;
  position: relative;
  margin-right: 1em;
  justify-content: center;
}

nav a:last-child {
  margin-right: 0;
}

nav a:after {
  background: none repeat scroll 0 0 transparent;
  bottom: 0;
  content: "";
  display: block;
  height: 2px;
  left: 50%;
  position: absolute;
  background: #fff;
  transition: width 0.3s ease 0s, left 0.3s ease 0s;
  width: 0;
}

nav a:hover:after {
  width: 100%;
  left: 0;
}


#logo {
    height: 2em;
    font-family: 'Merriweather', serif;
    font-size: 2em;
    font-weight: 700;
    margin: auto;
    padding: 0.5em 1em 0.5em 1em;
}

#logo a{
    color: white;
    text-decoration: none;
}

#logo a:hover{
    color: black;
}

/* TIPOGRAFIA*/

h1 {
  font-family: 'Merriweather', serif;
  font-size: 1.5em;
}

h2 {
  font-family: 'Merriweather', serif;
  font-weight: 300;
  font-size: 1.5em;
}
h5 {
  font-family: 'Merriweather', serif;
  font-size: 1rem;
  color: black;
  text-align: center;
}
.arriba {
  position: fixed;
  font-size: 1em;
  bottom: 1em;
  right: 1em;
  background-color: #82acbb;
  border-color: white;
  border-radius: 3em;
  width: 2em;
  height: 2em;
  text-align: center;
  padding: 1em;
}

/*INDEX*/

img {
  height: 12em;
}

img:hover {
  opacity: 0.5;
  transition: 0.5s ease-out;
}

.datos {
  font-family: 'Merriweather', serif;
  font-size: 0.75rem;
  line-height: 2em;
}

/* FOOTER */

footer {
	color: white;
	background-color: #5f7c79;
  padding: 1em;
  font-family: 'Open sans', sans-serif;
  list-style: none;
  bottom: 0;
}

.footer ul, .acreditacion ul {
  list-style: none;
  text-decoration: none;
  display: flex;
  justify-content: center;
}

.footer {
  font-size: 0.75rem;
  line-height: 180%;
}

.footer a, .acreditacion a {
	text-decoration: none;
	color: white;
  padding: 1em;
}

.footer a:hover{
  color: #82acbb;
}

footer img{
  width: 5em;
  height: 5em;
  padding: 0.5em;
}

.acreditacion{
  font-size: 0.75rem;
  line-height: 2em;
  margin-top: 2em;
  display: block;
  text-align: center;
}

.acreditacion ul{
  padding: 0;
}


footer {
  padding: 1em;
}

.footer {
  display: inline-block;
  justify-content: space-between;
  align-items: center;
  display: inline-block;
}

.footer ul, .footer li {
  display: block;
  padding: 0;
}

#rrss{
  padding-right: 1em;
  display: block;
}


.footer li::after {
  font-weight: 700;
  padding-right: 1em;
}

li:last-child:after{
  display: none;
}



/*LISTADO RECETA*/

.listado {
  text-align: center;
}

.listado a {
  text-decoration: none;
  color: white;
}

.listado button {
  background-color: #5f7c79;
  text-decoration: none;
  border-style: none;
  color: white;
  font-family:  'Merriweather', serif;
  font-size: 1em;
  padding: 0.5em;
  width: 10em;
  border-radius: 2em;
  text-align: center;
}

.listado button:hover {
  background-color: grey;
}

.listado h1 {
  text-align: center;
}

.listado h2 {
  color: #5f7c79;
}

.box {
  width: 100%;
  height: 300px;
}

.box img {
  height: 300px;
  width: 100%;
}

.box2 {
  height: 300px;
  width: 100%;
}

.box2 img {
  width: 100%;
  height: 300px;
}

.cover {
  object-fit: cover;
}

.listado p {
  margin: 1em;
}
/* DETALLES */

h4 {
  font-family: 'Open sans', sans-serif;
  font-size: 18px;
  font-weight: 700;
}

aside {
  text-align: left;
  margin-right: 2em;
  margin-bottom: 2em;
}

.apartados {
  text-align: left;
}

.apartados p {
  font-family: 'Open sans', sans-serif;
  font-weight: 700;
  padding-bottom: 0.5em;
}

.container {
  margin-bottom: 1em;
  text-align: center;
}


/*DETALLE RECETA*/

h2 {
  font-weight: 700;
  text-align: left;
}

h3 {
  font-weight: 700;
  border-bottom: 1px solid black;
  text-align: left;
}

.detalles p {
  line-height: 1.5em;
  padding-left: 1em;
}

.explicacion p {
  text-align: left;
  line-height: 1.5em;
}


.recetas img {
  width: 100%;
  height: auto;
}

h2{
  text-align: center;
}

.ficha {
  padding: 1em;
}

.anterior {
  background-color: #5f7c79;
  text-decoration: none;
  border-style: none;
  color: white;
  font-family:  'Merriweather', serif;
  font-size: 1em;
  padding: 0.5em;
  width: 10em;
  border-radius: 2em;
  float:left;
  margin-bottom: 1em;
  margin-left: 1em;
}

.siguiente {
  background-color: #5f7c79;
  text-decoration: none;
  border-style: none;
  color: white;
  font-family:  'Merriweather', serif;
  font-size: 1em;
  padding: 0.5em;
  width: 10em;
  border-radius: 2em;
  float: right;
  margin-right: 1em;
  margin-bottom: 1em;
}

button:hover{
  background-color: grey;
}

.nosotros p {
    width:80%;
    margin: auto;
    padding-bottom: 3em;
    padding-top: 2em;
}


/* SOBRE NOSOTROS */

.clip-svg {
  clip-path: url(#corazon);
}


.animacion {
  clip-path: url(#corazon);
  animation: morph 5s ease-in-out infinite;
  height: 200px;
}

@keyframes morph {
  0% {
      transform: scale(1.2);
	}

	50% {
      transform: scale(1.4);
	}

	100% {
    transform: scale(1.2);
	}
}


/* ENLACES*/

.enlaces{
  padding: 1em;
  text-align: left;
}


.enlaces a{
  color:blue;
}

.escritor {
  -webkit-mask-box-image: polygon(50% 0%, 80% 10%, 100% 35%, 100% 70%, 80% 90%, 50% 100%, 20% 90%, 0% 70%, 0% 35%, 20% 10%);
  clip-path: polygon(50% 0%, 80% 10%, 100% 35%, 100% 70%, 80% 90%, 50% 100%, 20% 90%, 0% 70%, 0% 35%, 20% 10%);
}

.mask2 {
  mask-image: url(/img/autor3.png);
  -webkit-mask-repeat: no-repeat;
  -webkit-mask-size: cover;
  width:100%;
  height: auto;
  max-width: 400px;
}

.autor {
  text-align: left;
  display:inline-flex;
  padding-top: 2em;
}

.autor p{
  padding-left: 1em;
}

.autor img {
  height: 5em;
}

.autor a{
  color: green;
}


@media screen and (min-width: 430px) {

  .hero-image {
    background-image: url("/img/hero-tablet.jpg");
    clip-path: polygon(100% 0%, 100% 89%, 91% 98%, 79% 91%, 68% 100%, 53% 91%, 41% 100%, 26% 90%, 15% 100%, 0 89%, 0% 0%);
    min-height: 400px;
  }

  .box {
    width: 100%;
    height: 500px;
  }

  .box2 {
    width: 100%;
    height: 300px;
  }

  .box img {
    height: 500px;
    width: 100%;
  }

  .mask2 {
    mask-image: url(/img/autor3.png);
    -webkit-mask-repeat: no-repeat;
    -webkit-mask-size: cover;
    width:100%;
    height: auto;
  }
}

@media screen and (min-width: 830px) {

  .path {
    clip-path: polygon(18% 11%, 63% 6%, 84% 28%, 96% 63%, 84% 85%, 25% 93%, 0% 82%, 5% 29%);
  }

  .hero-image {
    background-image: url("/img/hero.jpeg");
  }

  /*GENERAL*/

  body {
    padding:0;
  }

  #logo {
      height: 1.8em;
  }

  .menu {
    display: flex;
    flex-direction: row;
    align-items: center;
    width: 100%;
    box-sizing: border-box;
    justify-content: space-between;
    background-color: #5f7c79;
    align-items: center;
  }

  .menu :nth-child(1) {
    order: 1;
  }

  .menu :nth-child(2) {
    order: 2;
    margin-right: 2em;
  }

  .frases {
    text-align: center;
  }

  nav {
    text-decoration: none;
    font-family: 'Open sans', sans-serif;
    color: #1c1c1c;
  }

  nav ul, nav li, nav a {
    display: inline-block;
  	list-style: none;
    text-decoration: none;
    color: black;
    margin-bottom: 0;
    margin-right: 1em;
  }

  nav a {
    text-decoration: none;
    color: white;
  }

  nav li{
  padding-bottom: 0
  }

  footer {
    padding: 1em;
  }

  .footer ul, .footer li {
    display: flex;
  }

  .footer {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }

  #rrss{
    padding-right: 1em;
  }


  .footer li::after {
    font-weight: 700;
    padding-right: 1em;
    padding-left: 1em;
  }

  li:last-child:after{
    display: none;
  }

  .acreditacion{
    text-align: left;
  }



  /*INDEX*/

  .descripcion {
    max-width: 50vw;
    margin: auto;
  }

  figure {
    display: inline-block;
  }

  section {
    padding-bottom: 2em;
  }

  .mask2 {
    mask-image: url(/img/autor3.png);
    -webkit-mask-repeat: no-repeat;
    -webkit-mask-size: cover;
    width:60%;
    height: auto;
  }

  /*LISTADO RECETA*/

  .listado {
    max-width: 80%;
    display: flex;
    flex: 90%;
    flex-wrap: wrap;
    margin: auto;
    justify-content: center;
  }

  .listado h2 {
    font-size: 20px;
    margin-bottom: 0;
  }

  .listado p{
    text-align: center;
  }

  .container {
    width: 40%;
    text-align: center;
    margin: 1em 1em 2em 1em;
  }

  .container-detalle {
    width: 100%;
    height: 100px;
    text-align: center;
  }

  .box {
    height: 500px;
    width: 100%;
  }

  .box2 {
    height: 300px;
    width: 100%;
  }

  /*DETALLE RECETA*/
  .ficha {
    display: grid;
    grid-template-columns: 2fr 3fr;
    grid-gap: 20px;
    width: 70%;
    margin: auto;
  }

  .explicacion {
    padding-bottom: 0;
    margin-left: 0;
  }

  .explicacion p {
    width: 100%;
    margin-right: 0;
    margin-left: 0;
    text-align: left;
  }

  .recetas img {
    width: 100%;
    height: auto;
    max-width: 800px;
  }
}
